INSERT INTO `recipes` (`id`, `name`, `id_dish`, `id_user`, `amount`, `materials`, `eater`, `time`, `step_1`, `image_1`, `step_2`, `image_2`, `step_3`, `image_3`, `step_4`, `image_4`, `step_5`, `image_5`, `step_6`, `image_6`, `step_7`, `image_7`, `level`, `status`, `number_like`, `number_view`, `created_at`, `updated_at`) VALUES
(3, 'Thịt băm bọc trứng sốt cà chua', 1, 2, 13, 'Thịt heo bằm 100 gr,\r\n Cà chua 2 quả,\r\n Đậu hũ non 50 gr,\r\n Hành tím 10 gr,\r\n Hành lá 5 gr,\r\n Bơ lạt 5 gr,\r\n Dầu ăn 10 ml,\r\n Nước 30 ml,\r\n Hạt nêm 2 muỗng canh,\r\n Đường trắng 2 muỗng cà phê,\r\n Tiêu 1/2 muỗng cà phê,\r\n Nước tương 1 muỗng canh,\r\n Nước mắm 2 muỗng canh', 2, 35, 'Đầu tiên là sơ chế các nguyên liệu. Trứng cút luộc chín sau đó bóc vỏ. Cắt hạt lựu lần lượt đậu hũ non, cà chua; băm nhỏ hành tím và cuối cùng là cắt nhỏ hành lá.', 'Cb8Z_1.jpg', 'Cho 50gr đậu hũ non, 5gr hành tím, 5gr hành lá vào tô thịt bằm. Sau đó nêm thêm 1tbs hạt nêm, 1tsp đường, 1/2 tsp tiêu, 1tbs nước tương, 1tbs nước mắm. Trộn đều các nguyên liệu và sau đó ướp khoảng 10-15 phút cho thịt ngấm đều gia vị bạn nhé. Lấy khoảng 12gr thịt đã tẩm ướp dàn đều lên tay và đặt 1 trái trứng cút vào giữa, vo tròn lại sao cho thịt ôm trọn lấy trứng là thành công nhé các bạn.', '6kqq_2.jpg', 'Làm nóng dầu ăn trên chảo, sau đó cho lần lượt các viên thịt vào, chiên cho thịt chín, bên ngoài vàng đều, cháy xém nhẹ một chút là cực ngon rồi nha.', 'G4H4_3.jpg', 'Phần tiếp theo là làm sốt cà chua phô mai, một phần linh hồn của món ăn đây rồi. Mình làm nóng chảo, rồi cho 5ml dầu ăn vào, tiếp theo đun chảy 10gr bơ lạt. Khi bơ lạt tan chảy thì cũng là lúc mình cho thêm hành tím phi lên cho dậy mùi thì cho cà chua vào chảo. Nêm sốt 1tbs nước mắm, 1/2 tbs đường, 2 tbs hạt nêm, đun tới lúc cà chua mềm nhũn và phấn sốt sền sệt là công đoạn làm sốt đã đi được một nửa rồi.', '1mui_4.jpg', 'Tiếp theo mình cho thêm 10ml nước, 10ml sữa tươi, 5gr sốt phô mai rồi quậy đều cho hỗn hợp hoà quyên với nhau, đun sôi lên thì cho thịt viên vào chảo đảo đều cho thấm sốt. Vậy là mình đã hoàn thành xong món ăn rồi đấy.', 'JOxQ_5.jpg', 'Đây là thành phẩm của mình, trông thiệt ngon mắt phải không nào.', 'U49Z_6.jpg', NULL, NULL, 1, 1, 4, 322, '2022-09-25 23:10:33', '2020-05-17 11:09:30'),
(4, 'Bánh canh trứng cút ngon', 27, 2, 10, 'Bột gạo 400 gr,\r\n Xương heo 300 gr,\r\n Sườn non 300 gr,\r\n Trứng cút 20 quả,\r\n Hành lá 2 cây,\r\n Ngo rí 2 nhánh,\r\n Hạt nêm 1 muỗng canh,\r\n Muối 1/2 muỗng cà phê,\r\n Bột ngọt 1/2 muỗng cà phê,\r\n Tiêu 1/2 muỗng cà phê', 2, 30, 'Rửa sạch hành lá và ngò rí, cắt nhỏ. Luộc chín trứng cút, bóc vỏ.', 'pL9k_1.3.jpg', 'Làm bánh canh: Bột gạo đổ ra 1 tô thật to, chừa 1 chút bột để làm bột áo. Đun nước sôi già rồi rót từ từ vào bột, tỉ lệ khoảng 3 bột 1 nước, rồi nhanh tay nhào bột. Vắt bột thành từng vắt to bằng quả cam, cán bột thành từng miếng mỏng. Lúc cán nhớ cho bột áo để bột không bị dính. Cắt bột thành sợi bánh canh vừa ăn.', 'osiw_1.7.jpg', 'Cách nấu nước dùng bánh canh xương heo: Cho xương vào nồi, nấu khoảng 1 tiếng. Nêm hạt nêm, muối, bột ngọt, tiêu. Chú ý hớt bọt để nước dùng trong. Khi thấy nồi nước đã ngọt, vớt xương ra, cho sườn vào đun thêm 1 lúc cho sườn đủ chín là được.', '8htP_1.9.jpg', 'Dùng 1 chiếc vá như hình trên cho bột vào, rồi nhúng vào nồi nước. Chú ý nhúng sao cho bột ngập nước. Đợi sôi 2 phút.', 'tPTn_1.11.jpg', 'Sau khi sợi bánh canh chín thì cho vào tô. Xếp trứng cút lên, múc nước sườn vào. Rắc hành lá và ngò rí. Nếu thích cay có thể cho thêm ớt. Bánh canh xương trứng cút với hương vị đậm đà, thơm ngon, mời cả nhà ăn sáng rất hấp dẫn đấy nhé!', 'jiJs_1.12.jpg', '', '', NULL, NULL, 1, 1, 3, 322, '2022-10-01 06:58:44', '2020-05-17 11:10:52'),
(5, 'Xôi vò ngon', 28, 2, 5, 'Gạo nếp 500 gr,\r\n Đậu xanh 250 gr,\r\n Muối 1/2 muỗng cà phê,\r\n Đường trắng 2 muỗng canh,\r\n Nước cốt dừa 50 ml', 4, 60, 'Ngâm đậu xanh qua đêm rồi vo sạch, cho vào nồi nấu chín với 1/2 muỗng cà phê muối.\r\n Dùng chày giã nhuyễn đậu xanh đã chín rồi vo lại thành từng nắm.\r\n Bạn cũng có thể dùng muỗng tán nhuyễn mịn đậu xanh nếu không có chày nhé! Xôi vò ngon chính là chọn được đậu xanh còn mới, hạt nhỏ, không bị lẫn nhiều tạp chất.', 'GnlM_2.3.jpg', 'Vo sạch gạo nếp, ngâm qua đêm, để ráo nước.\r\n Bước ngâm gạo nếp qua đêm rất quan trọng. Nhờ bước này, xôi vò khi nấu lên sẽ được dẻo, xốp mềm và không bị sượng.', '4lz8_2.4.jpg', 'Cho gạo nếp vào khăn sạch, lau cho thật ráo nước.\r\n Chọn các loại khăn có độ thấm hút nước tốt để lau. Gạo nếp càng ráo thì nấu xôi vò càng ngon, càng khéo', 'PKj4_2.6.jpg', 'Cho gạo nếp vào khăn sạch, lau cho thật ráo nước.\r\n Chọn các loại khăn có độ thấm hút nước tốt để lau. Gạo nếp càng ráo thì nấu xôi vò càng ngon, càng khéo.\r\n Cách nấu xôi vò: Cho gạo nếp vào chõ hấp xôi vò. Hấp khoảng 15 phút thì cho 2 muỗng canh đường và 50ml nước cốt dừa vào, trộn đều. Hấp thêm khoảng 5-7 phút nữa cho xôi vò chín rồi đổ ra đĩa lớn.\r\n Trong quá trình hấp, nhớ lấy khăn sạch lau khô nắp chõ để nước không đọng, rơi xuống làm ẩm, ướt xôi vò.', 'ps0a_2.7.jpg', 'Nhanh tay dùng đũa xới đều xôi vò với 1/4 đậu xanh bào còn lại. Trộn cho xôi thật tơi.', 'OGRf_2.8.jpg', 'Cho xôi vò ra đĩa và tha hồ thưởng thức. Xôi vò ăn nóng hay nguội đều có vị ngon riêng. Nếu buổi sáng không có thời gian ăn tại nhà thì bạn cho xôi vò vào từng túi và đưa cho mọi người trong gia đình mang đến trường, nơi làm việc để ăn. Cách nấu xôi vò đơn giản quá chừng phải không, có thể bảo quản trong tủ lạnh khoảng 2 ngày. Bạn nhớ bọc kín xôi trong túi zip hoặc đậy đĩa/ tô xôi bằng màng bọc thực phẩm để xôi vò không bị khô nhé!', 'J3B7_2-Xôi vò.jpg', NULL, NULL, 3, 1, 2, 320, '2022-10-01 07:05:03', '2020-05-16 18:38:44'),
(6, 'Bánh canh thịt vịt', 29, 2, 14, 'Thịt vịt 1/2 con,\r\n Nấm bào ngư 300 gr,\r\n Gừng 1 củ,\r\n Hành lá 2 cây,\r\n Rượu trắng 200 ml,\r\n Hạt nêm 1/2 muỗng cà phê,\r\n Muối 3/2 muỗng cà phê,\r\n Đường trắng 1 muỗng cà phê,\r\n Hành tím băm 1 muỗng cà phê,\r\nNgo rí 10 gr,\r\n Dầu điều 2 muỗng cà phê,\r\n Bột gạo 400 gr,\r\n Bột năng 50 gr,\r\n Tiêu 1/2 muỗng cà phê', 4, 40, 'Cách làm bánh canh bột xắt: Trộn lẫn bột gạo với bột năng ra thau lớn, thêm 1/2 muỗng cà phê muối, trộn đều. Đổ từ từ nước sôi nóng già, vừa đổ vừa dùng muôi gỗ trộn bột. Dùng tay nhồi đến khi hỗn hợp bột thành mịn, dẻo. Cán cho cục bột dẹt ra, mỏng đều. Dùng dao bén cắt bột thành từng sợi dài, khi cắt nhớ áo một ít bột để chống dính. Cắt bỏ chân nấm, rửa sạch, ngâm vào thau nước lạnh có pha một ít muối khoảng 10 phút. Rửa sạch hành lá và ngò rí, cắt nhỏ.', '34bB_3.5.jpg', 'Rửa sạch gừng, giã gừng, trộn chung với rượu trắng. Vịt rửa sạch, chặt nhỏ, chà hỗn hợp gừng và rượu trắng lên khắp thân vịt, để khoảng 20 phút sau đó rửa lại cho thật sạch, để vịt lên rổ cho ráo nước.', 'n4lP_3.7.jpg', 'Phần vịt sau khi ráo nước, cho ra thau lớn, thêm hành tím băm, 1 muỗng cà phê muối, đường, hạt nêm, tiêu và một ít gừng giã nhuyễn, trộn đều, ướp khoảng 2-3 tiếng.', 'tS3h_3.8.jpg', 'Đun nóng dầu điều, phi tỏi thơm, cho hỗn hợp thịt vịt đã ướp vào nồi, đun lửa lớn đến khi sôi thì đun lửa nhỏ, đổ 1.5 lít nước lạnh vào nồi đun sôi đến khi vịt mềm.', 'mqUw_3.9.jpg', 'Thả nấm vào nồi vịt đang đun, nêm nếm lại gia vị cho vừa ăn. Thả sợi bánh canh đã cắt vào nồi thịt vịt, đun sôi đến khi sợi bánh canh nổi trong, nêm nếm lại gia vị tùy theo khẩu vị.', 'NMpJ_3.10.jpg', 'Tắt bếp, thêm hành lá, ngò rí vào. Múc bánh canh vịt ra chén/tô dùng nóng. Với cách nấu bánh canh vịt, nếu thích bạn có thể thêm tiết lợn, hay trứng cút vào nồi bánh canh, hương vị sẽ thêm thơm ngon nhé. Vào bếp làm ngay bánh canh vịt đãi cả nhà ăn sáng ngon miệng nhé!', 'RyAM_3.11.jpg', NULL, NULL, 2, 1, 3, 320, '2022-10-01 07:09:30', '2020-05-17 10:47:20'),
(7, 'Bánh canh chả cá', 30, 2, 12, 'Bột gạo 2 chén,\r\n Bột năng 250 gr,\r\n Phi lê cá 500 gr\r\n(Cá thác lác),\r\n Ớt 1 trái,\r\n Xương ống 1 kg,\r\n Hành lá 1/2 cây,\r\n Nước mắm 2 muỗng canh,\r\n Đường trắng 1 muỗng cà phê,\r\n Tiêu 1/2 muỗng cà phê,\r\n Ngò rí 5 gr,\r\n Hạt nêm 1 muỗng cà phê,\r\n Muối 1/2 muỗng cà phê', 4, 45, 'Ninh nước dùng từ xương lợn khoảng 2 giờ, trong quá trình đun nhớ vớt bọt để nước dùng được trong.', 'tNWP_4.1.jpg', 'Trộn bột năng với bột gạo với nhau. Đun nước sôi, rưới đều lên bột rồi nhồi đến khi hỗn hợp bột đó mềm mịn, dai là được. Chia bột thành từng viên tròn bằng nắm tay, dùng chai cán mỏng rồi thái thành từng sợi to bằng đầu đũa.\r\n Để tiết kiệm thời gian làm bánh canh chả cá, bạn có thể tìm mua bánh canh bột gạo bán sẵn ở các chợ và siêu thị.', 'PP5u_4.2.jpg', 'Quết nhuyễn thịt phi lê cá với đầu hành, muối, đường, hạt nêm, tiêu. Quết đến khi thấy phần thịt cá dai, dẻo dính vào nhau là được.\r\n Cách làm bánh canh chả cá cực nhất là ở bước này đó. Quết càng mạnh tay và càng lâu thì chả cá càng dai ngon.', 'Lvu6_4.3.jpg', 'Vớt bỏ phần xương trong nước dùng bánh canh chả cá, nêm nước mắm, hạt nêm vào nước dùng cho vừa ăn. Dùng muỗng chia từng viên chả cá thành miếng vừa ăn rồi cho vào nồi nước dùng, khuấy đều.\r\n Nếu có thời gian thì có thể chiên hoặc hấp chả cá để bánh canh chả cá hấp dẫn hơn.', 'xXug_4.4.jpg', 'Đun sôi một nồi nước khác, cho sợi bánh canh vào chần sơ qua. Xả lại bằng nước lạnh rồi để ráo. Khi nước dùng bánh canh chả cá đã chuẩn bị xong, cho tiếp sợi bánh canh đã chần vào rồi đun sôi, nêm nếm lại cho hợp khẩu vị gia đình nếu cần.', 'mvwr_4.5.jpg', 'Múc bánh canh chả cá ra tô và thưởng thức. Bánh canh chả cá dùng nóng với ít hành ngò, thêm chén nước mắm ớt kế bên là hết sảy. Vào những ngày mưa hay tiết trời se lạnh được măm măm 1 tô bánh canh chả cá nóng hôi hổi thì còn gì bằng! Cuối tuần các bạn nhất định phải thử cách làm bánh canh chả cá này để đãi gia đình nhé!\r\n Cho xương lợn vào túi lưới rồi mới cho vào nồi hầm. Túi lưới sẽ giúp nước dùng bánh canh chả cá không bị lẫn những mảnh xương nhỏ, ăn ngon hơn. \"', 'JKMH_4.6.jpg', NULL, NULL, 2, 1, 2, 321, '2022-10-01 07:12:48', '2020-05-17 11:11:05'),
(8, 'Bánh canh cá lóc Quảng Trị', 31, 2, 7, 'Cá 1 con,\r\n Bột gạo 300 gr,\r\n Kẹo caramel 200 lá,\r\n Đường 200 gr,\r\n Tiêu 50 gr,\r\n Cá xay 500 gr,\r\n Ớt 3 trái', 4, 30, 'Chặt cá lóc thành miếng vừa ăn, rửa sạch nguyên liệu. Luộc cá vừa chín tới, vớt cá ra ngoài. Phi hành cùng ớt chín vàng. Cho hỗn hợp vừa phi chín tới vào nước cá đã luộc.', 'ftsb_5.2.jpg', 'Cách nấu bánh canh cá lóc: Tách thịt cá ra khỏi xương lựa phần nạc bỏ riêng sang 1 tô, tiếp tục cho nước đun sôi trong 10 phút. Cho cá xay đã vo viên tròn (nếu các bạn khéo tay tạo hình cho cá viên thành hình tròn sẽ ngon mắt hơn) vào trong nấu chín. Cho gia vị đã chuẩn bị vào nêm vừa ăn. Chú ý: nêm tiêu vào lúc cuối cùng vì tiêu chính là hương vị chính làm dậy mùi của món ăn.', '56Yc_5.5.jpg', 'Cho hành lá đã thái nhỏ lên bên trên, trang trí ra tô. Vậy là đã có món bánh canh cá lóc đúng điệu đất Quảng Trị rồi. Hãy lưu lại cách làm bánh canh cá lóc đặc trưng của vùng đất Quảng Trị nhé. Hứa hẹn sẽ là món banh cá lóc đổi vị cho cả nhà cuối tuần đấy.', 'jkKW_5.7.jpg', '', '', '', '', '', '', NULL, NULL, 1, 1, 2, 320, '2022-10-01 07:15:29', '2020-05-17 10:51:56'),
(9, 'Bánh burger thịt heo', 32, 2, 11, 'Thịt heo bằm 283 gr,\r\n Bánh mì 2 cái\r\n(Burger),\r\n Sốt tương đen 2 muỗng canh,\r\n Gừng 1 miếng,\r\n Giấm 1 muỗng canh,\r\n Củ cải đỏ 85 gr,\r\n Sốt Mayonnaise 2 muỗng canh,\r\n Bông cải xanh 9/4 gr,\r\n Muối 3/2 muỗng cà phê,\r\n Tiêu 3/2 muỗng cà phê,\r\n Dầu olive 1 muỗng canh', 2, 25, 'Rửa sạch củ cải đỏ, bông cải xanh, để ráo nước. Bông cải xanh cắt bỏ phần thân, thái nhỏ. Gừng rửa sạch, gọt vỏ, thái nhỏ. Bánh burger cắt làm đôi. Củ cải thái lát mỏng, cho củ cải vào bát ướp cùng giấm và dầu olive. Trong một chiếc bát nhỏ, cho mayonnaise, 1/2 phần sốt tương đen, 1/2 muỗng cà phê muối và hạt tiêu vào khuấy đều, để riêng.', 'Xong_6.1.jpg', 'Chế biến bông cải xanh: Cho bông cải xanh lên khay nướng, cho dầu olive, 1/2 muỗng cà phê muối và hạt tiêu vào trộn đều. Cho khay súp lơ vào lò và nướng khoảng 16-18 phút hoặc đến khi bông cải chín, chuyển màu. Tắt lò, lấy khay bông cải ra, cho dầu mè vào trộn đều.\r\n Cách làm hamburger thịt heo này ngon hơn là nhờ có bông cải nướng này đó, các bạn không được bỏ qua nha !', 'UF4f_6.2.jpg', 'Cách làm nhân bánh hamburger: Trong một chiếc bát, cho thịt heo xay, gừng thái nhỏ, phần nước sốt tương đen còn lại, thêm 1/2 muỗng cà phê muối và hạt tiêu vào trộn đều. Chia hỗn hợp thịt thành 2 phần bằng nhau có độ dày khoảng 1,25cm. Để riêng ra đĩa. Đun nóng chảo cùng chút dầu olive, cho thịt heo đã viên vào chiên mỗi mặt khoảng 3 - 5 phút hoặc cho đến khi thịt chín, sém cạnh. Gắp ra đĩa, dùng giấy bạc bọc lại để thịt nóng.', '7oZU_6.3.jpg', 'Nướng bánh burger: Cho bánh mì vào áp chảo khoảng 1 - 2 phút cho đến khi bánh mì sém cạnh. Gắp ra đĩa để riêng. Cho bánh mì lên đĩa, rưới nước sốt lên trên bánh mì, xếp thịt heo rồi củ cải đỏ lên rồi kẹp miếng bánh còn lại lên trên cùng. Ăn bánh burger cùng súp lơ xanh vừa nướng sẽ rất ngon, lại đầy đủ dinh dưỡng.', 'tPHf_6.4.jpg', 'Bánh burger thịt heo với lớp vỏ mềm, củ cải giòn, nước sốt béo ngậy bọc lấy nhân thịt heo xay đậm đà, ăn kèm bông cải xanh nướng vừa ngon vừa đầy đủ dinh dưỡng. Cách làm bánh burger heo lại không hề khó, các bạn có thể tự làm ở nhà để giúp bữa ăn sáng của gia đình thêm phần thú vị nha !', 'cAEq_6.6.jpg', '', '', NULL, NULL, 2, 1, 2, 320, '2022-10-01 07:24:08', '2020-05-17 10:51:59'),
(10, 'Bánh canh cá lóc miền Tây', 33, 2, 13, 'Cá lóc 500 gr,\r\n Hạt nêm 1/2 muỗng canh,\r\n Muối 1/2 muỗng canh,\r\n Cà chua 1 trái,\r\n Ngò rí 20 gr,\r\n Tiêu 1 muỗng cà phê,\r\n Hành tím 1 củ,\r\n Chả cá 500 gr,\r\n Hành lá 20 gr,\r\n Hẹ 20 gr,\r\n Giá đỗ 20 gr,\r\n Nước 2 lít,\r\n Bánh canh 400 gr', 4, 25, 'Sơ chế cá lóc, thái mỗi con làm 5 phần. Lưu ý cá lóc khi mua về nên được xát chanh hoặc muối để cá sạch bạn nhé. Bỏ cá lóc vào nồi và đổ khoảng 200ml nước (hoặc 1/3 dung tích nồi nấu). Bật lửa nấu trong khoảng 10 - 15 phút sau đó tắt bếp và vớt cá ra, để ráo khoảng 5 phút.\r\n Sở dĩ không đổ nước vào nhiều là do thịt cá rất dễ bị bở, ngoài ra bước này bạn khoan hãy phi hành. Một số ý cho rằng nên chiên cá hoặc phi hành sẽ khử được mùi tanh, thế nhưng như vậy sẽ khiến bánh canh của bạn trở nên đầy dầu mỡ. Chỉ cần xát chanh và muối ở bước sơ chế là được rồi bạn nhé, cá sẽ giữ được độ ngọt và mềm', 'qne9_7.1.jpg', 'Chuẩn bị nguyên liệu đi kèm, bao gồm: chả cá, giá, hẹ, hành, ngò, cà chua và bánh canh. Chả cá mua ngoài hay bị gắt dầu, tốt hơn hết hãy chần sơ qua nước nóng bạn nhé. Rửa sạch giá, hẹ, hành, ngò và cà chua. Sau đó thái hành, hẹ ra từng khúc, cà chua xắt dọc làm 4 để nấu cùng nước lèo. Về bánh canh, khi mua về hãy chịu khó chần qua nước đun sôi bạn nhé, bánh sẽ ngon hơn và khử được mùi chua vốn có.\r\n Cần rửa sạch gốc hẹ và hành bạn nhé, nên dùng muối để ngâm rau.', 'EToO_7.2.jpg', 'Nấu bánh canh cá lóc miền Tây: dùng lại phần nước đã luộc cá trước đó, đổ gần đầy nồi nấu, bỏ cà chua đã thái vào nồi. Cho 1/2 muỗng canh muối và 1/2 muỗng canh hạt nêm, tùy bạn thích ăn mặn ngọt như nào có thể bổ sung thêm nhé.\r\n Giá, hẹ nên được trần riêng ở nồi nước sôi khác nhé, để tránh nước lèo của bạn có vị \"\"lạ\"\".', 'qL91_7.3.jpg', 'Đậy nắp đung sôi và trong thời gian này bạn có thể tiến hành rút xương cá, chuẩn bị tô cho cả nhà rồi! Tăng hương vị cho nước dùng thông qua hành tím phi và chút ngò, hành nhé.\r\n Khi hành đã được phi xong, bạn tắt bếp và bỏ ngay vào chảo ngò và tiêu nhé, sau đó bỏ hỗn hợp vào nồi nước dùng. Đây là bí kíp khá hay để tăng hương thơm cho các loại nước dùng đấy!', '3nFU_7.6.jpg', 'Bước cuối cùng là đổ nước dùng vào tô đã chuẩn bị sẵn. Bánh canh cá lóc thành phẩm có hương vị thơm ngon, ngọt tự nhiên của cả lóc. Đặc biệt với cách nấu bánh canh cá lóc miền Tây này, nước dùng sẽ cực đậm vị, lại giúp thanh nhiệt cho ngày nắng nóng. Chúc cả nhà thành công!', 'e8hX_7.8.jpg', '', '', NULL, NULL, 1, 1, 2, 320, '2022-10-01 07:26:59', '2020-05-17 10:52:00'),
(11, 'Bánh burger mini', 34, 2, 7, 'Bột mì 200 gr,\r\n Bơ 14 gr,\r\n Muối 5 gr,\r\n Đường trắng 16 gr,\r\n Trứng gà 1 quả,\r\n Sữa tươi 100 gr,\r\n Mè trắng 1 chén', 2, 100, 'Trộn bột làm bánh burger: bột mì, bơ, muối, đường, trứng, sữa tươi vào trộn đều với nhau rồi đem ủ ở nhiệt độ thường khoảng 1 tiếng.', 'g0aZ_8.1.jpg', 'Sau đó chia bột thành 9 phần đều nhau, tiếp tục ủ trong 50 phút nữa trong nhiệt độ (35-40 độ C).', '9j3u_8.2.jpg', 'Sau khi ủ bột xong, rắc mè trắng lên trên rồi đem nướng ở 200 độ C trong 10 phút.', 'deic_8.3.jpg', 'Bánh gurger chín vàng đem ra. Tròn tròn, nhỏ nhắn, thơm thơm, rất tiện để mang đi ăn trong các buổi dã ngoại nữa đấy! Bánh burger sau khi chia bột xong các bạn có thể bọc lại để tủ lạnh sáng rã đông rồi ủ lần 2 cho lớn là nướng bình thường rồi, rất tiện phải không nào. Có thể cắt đôi, kẹp rau xà lách, thịt nguội, phô mai vào trong, ăn chả khác gì ngoài tiệm luôn.', 'qIZK_8.5.jpg', '', '', '', '', NULL, NULL, 2, 1, 1, 320, '2022-10-01 07:28:42', '2020-05-16 18:38:58'),
(12, 'Bánh canh cá bớp', 3, 2, 18, 'Cá bớp 500 gr,\r\n Xương heo 300 gr,\r\n Cà rốt 200 gr,\r\n Củ cải trắng 200 gr,\r\n Nấm rơm 150 gr,\r\n Trứng cút 10 quả,\r\n Bánh canh 300 gr\r\n(Tươi),\r\n Hành lá 50 gr,\r\n Ngo rí 50 gr,\r\n Hành tím 50 gr,\r\n Đường phèn 50 gr,\r\n Muối 1 muỗng cà phê,\r\n Nước mắm 2 muỗng cà phê,\r\n Bột canh 1 muỗng canh,\r\n Tiêu 1 muỗng cà phê,\r\n Dầu ăn 1 muỗng canh,\r\n Ớt 4 quả,\r\n Chanh 1 trái', 4, 55, 'Chặt nhỏ xương heo, rửa sạch, chần qua nước sôi. Sau đó, vớt xương ra rồi cho lượng nước lọc vừa đủ vào nồi để hầm xương.', 'z0NZ_10.1.jpg', 'Gọt vỏ cà rốt, củ cải, rửa sạch tỉa hoa (tùy thích). Rửa sạch nấm rơm, ngâm nước muối 15 phút rồi vớt ra để ráo. Rửa sạch cá bóp thái miếng nhỏ vừa rồi ướp với muối. Luộc trứng cút, bọc vỏ. Cọng bánh canh chần sơ qua nước sôi 2 phút rồi vớt ra để ráo. Hành tím phi thơm, ớt đỏ thái khoanh.', 'tgkU_10.3.jpg', 'Khi nồi xương hầm sôi khoảng 20 phút thì tiếp tục cho cà rốt và củ cải vào nấu cho đến khi chín mềm. Kế tiếp, cho cá bóp vào nấu tiếp trên lửa vừa.', 'id8B_10.9.jpg', 'Khi nồi nước dùng sôi lên lần nữa thì cho nấm rơm vào rồi nêm nếm gia vị muối, bột canh, đường phèn theo liều lượng tùy khẩu vị gia đình. Tiếp đó, cho hành lá và tiêu xay vào thì tắt bếp', 'iuOY_10.10.jpg', 'Cho cọng bánh canh vào tô rồi gắp cá bóp với cà rốt, củ cải, hành ngò vào tô rồi chan nước dùng lên, dùng nóng. Nước dùng thanh ngọt, đậm đà với từng miếng cá tươi ngon, mềm thơm, béo ngậy quyện với những cọng bánh canh trắng ngần, thật hấp dẫn. Đây là món bánh canh cá bớp tươi, thơm ngon, dễ nấu, bạn có thể nấu cho cả nhà thưởng thức mà không tốn quá nhiều thời gian.', 'cGht_10.11.jpg', '', '', NULL, NULL, 1, 1, 1, 320, '2022-10-01 07:31:11', '2020-05-16 18:39:00'),
(13, 'Bánh canh bột xắt nước dừa nấu tôm', 36, 2, 6, 'Dừa nạo 300 gr,\r\n Tôm tươi 300 gr\r\n(Tôm đất),\r\n Bánh canh 500 gr\r\n(Bột gạo),\r\n Nước mắm 1/2 muỗng canh,\r\n Bột ngọt 1/2 muỗng cà phê,\r\n Muối 1/2 muỗng cà phê', 2, 25, 'Cho nước ấm vào vắt dừa ra nước cốt', 'x1bb_11.1.jpg', 'Lột vỏ tôm, giữ lại phần gạch tôm', 'WSAe_11.2.jpg', 'Rửa bánh canh cho sạch phần bột phủ bên ngoài', 'FGsS_11.3.jpg', 'Cách nấu bánh canh bột gạo nước dừa: Cho nồi nước cốt dừa lên bếp, nấu sôi vừa. Nêm muối. Sau đó cho bánh canh vào. Cho tôm vào đảo đều tay, để tôm chín sẽ có màu đỏ cam rất đẹp. Nêm bột ngọt.\r\n Khuấy đều tay, nếu ko bánh canh sẽ dính nồi', 'F4rx_11.5.jpg', 'Múc bánh canh bột xắt nước dừa nấu tôm ra tô, cho hành ngò vào. Nêm mỗi tô 1/2-1 muỗng canh nước mắm tùy theo khẩu vị. Với cách nấu bánh canh bột gạo nước dừa này, cực đơn giản nhưng lại rất thơm ngon đấy nhé. Còn lăn tăn gì mà không làm ngay món bánh canh bột xắt mời cả nhà nào!', 'H3KC_11.6.jpg', '', '', NULL, NULL, 2, 1, 1, 320, '2022-10-01 07:33:44', '2020-05-16 18:39:01'),
(14, 'Bánh canh bột lọc', 37, 2, 11, 'Bột năng 400 gr,\r\n Cá lóc 250 gr,\r\n Hành lá 100 gr,\r\n Hành tím 1 củ,\r\n Dầu ăn 2 muỗng canh,\r\n Ớt 2 trái,\r\n Nước mắm 1 muỗng canh,\r\n Nước màu 1 muỗng canh,\r\n Hạt nêm 1 muỗng cà phê,\r\n Bột ngọt 1/2 muỗng cà phê,\r\n Ớt bột 1/2 muỗng cà phê', 2, 35, 'Làm sạch cá lóc, cho vào nồi nước, luộc chín, giữ lại phần nước luộc cá. Vớt cá lóc ra, róc lấy thịt, bỏ xương, cho ra đĩa.', 'jDyl_12.1.jpg', 'Rửa sạch hành tím, hành lá, ớt, cắt nhỏ, cho ra đĩa.', 'lglD_12.2.jpg', 'Cách làm bánh canh bột lọc: Cho bột năng vào một mặt phẳng rộng, tạo một trũng sâu ở giữa. Đổ từ từ nước nóng vào giữa, dùng tay trộn đều thành một hỗn hợp dẻo, mềm.', 'N6eq_12.4.jpg', 'Cán mỏng bột, dùng dao cắt bột thành sợi dài khoảng 7cm. Sau đó, cho sợi bánh canh vào nồi nước sôi, luộc chín.', 'osrW_12.6.jpg', 'Cách nấu bánh canh bột lọc: Phi thơm hành tím với 2 muỗng canh dầu ăn. Cho cá lóc, 1 muỗng canh nước mắm, 1 muỗng canh nước màu, 1 muỗng cà phê hạt nêm, 1/2 muỗng cà phê bột ngọt, 1/2 muỗng cà phê ớt bột vào, đảo đều 1 phút. Lọc nước luộc cá qua rây, cho vào ngập mặt cá lóc, nấu sôi để làm nước dùng.', 'SvVa_12.7.jpg', 'Vớt sợi bánh canh ra rổ, để ráo, cho vào tô. Cho cá lóc, chan nước vào. Rắc hành lá, ớt lên trên và thưởng thức! Tô bánh canh bột lọc thành phẩm có sợi bánh canh dai dai, cùng với nước dùng đậm vị, thơm lừng, đảm bảo sẽ chiều lòng cả thực khách khó tính nhất đấy!', 'HAKR_12.8.jpg', NULL, NULL, 2, 1, 1, 320, '2022-10-01 07:36:22', '2020-05-16 18:39:02'),
(15, 'Bánh mì Việt Nam', 38, 2, 6, 'Bột mì 300 gr,\r\n Bơ 20 gr,\r\n Men nở 8 gr,\r\n Đường trắng 15 gr,\r\n Muối 4 gr,\r\n Dầu ăn 1 muỗng cà phê', 3, 55, 'Trước khi làm bánh mì Việt Nam cần hoà men nở vào 190ml nước ấm để kích hoạt men khoảng 5 phút (chú ý nước chỉ ấm khoảng 20 độ C). Cách làm bánh mì Việt Nam: Trộn đều bột mì, muối, đường trắng trong 1 cái tô. Sau đó, đổ hỗn hợp men nở vào cùng, trộn đều thành một hỗn hợp bột mềm, mịn.\r\n Cách làm bánh mì không bị vón cục bột: Rây bột qua 1 lần trước khi cho vào tô, bánh mì Việt Nam ra thành phẩm sẽ có phần ruột tơi xốp, mềm mịn.', 'g9mT_13.1.jpg', 'Tiếp tục cho 20gr bơ vào, nhào đều đến khi bột mịn. Rắc tí bột mì ra mặt phẳng bất kì, đặt bột bánh mì lên nhào tiếp khoảng 5-7 phút đến khi bột bánh mì dẻo mịn, không dính tay. Vo tròn khối bột bánh mì. Rưới 1 muỗng cà phê dầu ăn vào tô, đặt miếng bột bánh mì vào, đậy kín, ủ 1 giờ', 'ewEi_13.8.jpg', 'Lúc này, bột bánh mì Việt Nam sẽ nở gấp 3 lần. Tiếp tục rắc bột lên mặt phẳng và nhào lần 2 khoảng 5-7 phút. Đậy kín, ủ thêm 60 phút nữa. Bột bánh mì sau khi ủ sẽ nở thêm ra, sợi bột dai, có thớ', '7e1d_13.9.jpg', 'Tiếp tục nhào bột bánh mì cho vỡ hết bọt khí. Chuẩn bị tạo hình bánh mì Việt Nam. Ngắt bột bánh mì thành những phần nhỏ. Dàn đều bột bánh mì thành miếng tròn mỏng.\r\n Mỗi viên bột mình ngắt tầm 70gr. Nếu thích ăn bánh mì Việt Nam ổ lớn hơn thì bạn ngắt cục bột to hơn nhé!\r\n Gấp 2 bên miếng bột. Gấp phần đỉnh vào rồi cuộn lại. Bím phần mép và 2 đầu. Vo tròn dài, béo bụng.', 'ZZ3b_13.14.jpg', 'Xếp bánh mì lên khay nướng có lót sẵn 1 lớp giấy nến. Đậy khăn ủ 30 phút để bột nở ra.\r\n Làm nóng lò nướng ở nhiệt độ 180 độc C trong khoảng 15 phút trước khi nướng bánh. Làm nóng lò nướng ở nhiệt độ 180 độc C trong khoảng 15 phút trước khi nướng bánh. Rạch nhẹ bánh mì, bánh sẽ tự mở miệng ra sau 30 giây (có thể khía bánh chéo tuỳ thích). Bánh mì Việt Nam nưởng ở nhiệt độ 170 độ trong 10 phút đầu. lấy khay bánh mì nướng ra xịt đẫm nước và nướng tiếp 10-12 phút nữa (nhiệt 150 độ) đến khi vỏ bánh mì Việt Nam vàng đều.\r\n Phun đẫm nước trước khi nướng để bánh mì Việt Nam khi nướng được mỏng vỏ và không bị nứt.', '3xDg_13.16.jpg', 'Lấy vỉ bánh mì Việt Nam nướng ra ngoài, để hơi nguội 1 tí. Bánh mì Việt Nam thành phẩm mỏng vỏ, vàng giòn, ruột mềm xốp, thơm ngon.\r\n Cách làm bánh mì Việt Nam tuy nhiều bước nhưng mỗi bước đều không quá khó để thực hiện. Bắt tay vào làm bánh mì Việt Nam ngay hôm nay bạn nhé!', 'nOxH_13.18.jpg', NULL, NULL, 2, 1, 0, 320, '2022-10-01 07:40:10', '2022-11-21 08:17:20'),
(16, 'Bún mắm miền tây đúng điệu', 39, 2, 15, 'Mắm cá linh 300 gr,\r\n Dừa xiêm 1 trái,\r\n Mắm cá sặc 300 gr,\r\n Xương heo 500 gr,\r\n Cá loc 500 gr,\r\n Cà tím 2 trái,\r\n Cá hú 500 gr,\r\n Mực ống 300 gr,\r\n Tôm tươi 300 gr,\r\n Thịt heo quay 300 gr,\r\n\r\n Tỏi 1 muỗng canh,\r\n Sả băm 100 gr,\r\n Hành tím băm 2 muỗng canh,\r\n Ớt 10 gr,\r\n He 100 gr', 5, 60, 'Để nấu bún mắm ngon trước tiên cần lấy nước 1 trái dừa xiêm nấu cùng 3 lạng mắm cá linh và 3 lạng mắm cá sặc. Nấu riu riu trong gần 1 tiếng để mắm rục rã. Sau đó lọc qua ray để bỏ cặn, lấy nước mắm.\r\n Nếu dừa ít nước, bạn chủ động bạn lấy nước 2 trái cho vào đun cùng. Có thể dùng xương ống hoặc sườn già (khoảng 500gr). Rửa sạch, sau đó cho nước ngập xương kèm theo chút muối, đun sôi lên, để xương ra chất dơ, đổ nước dơ đó đi và bỏ xương lại vào nồi to, cho ngập nước lần nữa, thêm muối. Bắt đầu hầm với lửa nhỏ trong vòng 1 đến 1h30p.\r\n Đặc biệt, khi nấu bún mắm nếu mọi người mua được đầu cá lóc, xương cá để hầm thì càng ngọt nước và đúng vị (mua khoảng 500gr xương cá).', 'X6S9_14.1.jpg', 'Lấy 1 trái cà tím to, cắt miếng vừa ăn và ngâm vào nước muối khoảng 10 phút cho cà không bị đen. Bằm nhuyễn tỏi + ớt + hành tím + sả. Sau đó đun nóng dầu ăn và cho các gia vị này vào xào cho dậy mùi thơm, cuối cùng là cho cà tím vào xào khoảng 5 phút.', '6OSd_14.3.jpg', 'Sau khi hầm xương ra chất ngọt, gắp bỏ xương ra hết và đổ nồi nước mắm vào đun. Khi nước sôi, đổ cà tím và các gia vị vừa xào vào nồi nước, vặn nhỏ lửa, đun riu riu cho nước mắm hòa quyện vào nước hầm xương. Đun khoảng 30 phút đến 1 tiếng, tùy theo bạn nêm nếm nước dùng đã sắc và đậm đà hay chưa, cắt nhỏ vài cọng hẹ vào cho thơm nhé. Gia vị thì cho bột nêm, muối hoặc nước mắm tùy theo khẩu vị nêm nếm của mỗi người. Lúc này, bạn nào muốn nước dùng có vị béo thì có thể cho 2 - 3 khứa cá hú vào đun cùng, chọn khứa cá có chút mỡ sẽ rất ngon.', 'WKLh_14.4.jpg', 'Nấu xong phần nước dùng của món bún mắm là thấy mọi việc nhẹ nhõm. Còn lại chỉ là sơ chế sạch hải sản và luộc chín tôm lột vỏ, mực cắt khoanh bỏ da luộc. Heo quay cắt miếng vừa ăn.\r\n Nước luộc tôm mực, mọi người nên tận dụng đổ luôn vào nồi nước dùng bún mắm sẽ bao ngọt nhé.', 'WNkS_14.5.jpg', 'Rau ăn kèm bún mắm miền Tây thì thôi rồi nhiều vô kể, gồm: rau nhút, rau muống bào, bông súng, kèo nèo, rau má, rau đắng, giá, hẹ, có chỗ còn thêm bông bí, bông điên điển (nói chung ai muốn ăn gì thì mua đó).', 'K7fO_14.6.jpg', 'Thế là từ nay thèm thuồng bún mắm thì không phải chạy khắp nơi tìm quán roài. Ăn bún mắm ở nhà, ai siêng thì làm mắm me, không siêng thì pha chút nước mắm mặn cùng tí bột ngọt, xắt miếng ớt tươi vào cho cay, vắt miếng tắc vào cho chua chua. Rồi thì chấm hải sản mà làm tới thôi. Không nhiều thịt thà như ngoài quán vì em chỉ thích tôm, mực, heo quay nên làm mỗi 3 món này. Khoái bún mắm vì ăn được quá nhiều loại rau 1 lần.', 'zDIg_14.7.jpg', NULL, NULL, 3, 1, 0, 320, '2022-10-01 07:43:06', '2022-10-02 04:04:06'),
(17, 'Chân gà ngâm sả tắc cay ngon', 12, 15, 12, 'Chân gà 10 cái,\r\n Gừng 1 củ,\r\n Muối hột 1 muỗng cà phê,\r\n Lá chanh 6 lá,\r\n Sả 6 cây,\r\n Tỏi 4 tép,\r\n Trái tắc 4 trái,\r\n Nước mắm 40 ml,\r\n Đường trắng 40 gr,\r\n Giấm 20 ml,\r\n Ớt 2 trái,\r\n Nước cốt chanh 30 ml', 2, 35, 'Đun sôi 400ml nước với lá chanh, sả cắt khúc. Cho chân gà vào, luộc chín. Tránh luộc chân gà quá chín làm chân gà ngâm dễ bỡ và đổ nhớt', 'iTqK_1.4.jpg', 'Vớt chân gà luộc vừa chín tới ra, ngâm vào tô nước nước đá có pha 30ml nước cốt chanh sẽ giúp da gà săn lại, nước chanh giúp chân gà ngâm trắng và ít bị thâm. Vớt chân gà ra, cho vào tủ lạnh để chân gà se lại (Khâu làm lạnh và khô chân gà này không thể thiếu, để tránh làm chân gà ngâm bị nhớt nhé).', '3Qjz_1.6.jpg', 'Nấu nước làm chân gà ngâm ngon chuẩn: Đun 100ml nước với trái tắc, 40gr đường trắng, khuấy đều cho đường tan hết. Tắt bếp, để nguội, thêm lá chanh cắt sợi, sả cắt nhỏ, tỏi, 40ml nước mắm, 20ml giấm, ớt vào, khuấy đều.', 'tdzT_1.9.jpg', 'Cho hỗn hợp nước mắm, tắc vào hũ thủy tinh. Cho chân gà vào, đậy kín nắp, để chân gà ngâm sả tắc cay khoảng 1 ngày trong ngăn mát tủ lạnh là có thể ăn được.', 'Qd8B_1.1.jpg', 'Chân gà ngâm sả tắc cay là món ăn vặt thơm ngon, cho cả nhà nhâm nhi cực thỏa thích. Cách làm chân gà ngâm sả tắc đơn giản này thích hợp với nhiều bạn làm lần đầu.', 'UOL2_1.13.jpg', '', '', '', '', 2, 1, 0, 320, '2022-11-08 08:00:48', '2022-11-08 08:10:04'),
(18, 'Bánh chiffon cam ngon', 50, 15, 10, 'Lòng đỏ trứng gà 4 cái,\r\n Nước cốt cam 115 ml,\r\n Vỏ cam 1 muỗng canh,\r\n Đường trắng 150 gr,\r\n Bột mì 110 gr,\r\n Bột bắp 40 gr,\r\n Bột nở (baking powder) 1 muỗng cà phê,\r\n Dầu ăn 75 ml,\r\n Lòng trắng trứng gà 4 cái,\r\n(Tinh dầu),\r\n Cream of tartar 1/2 muỗng cà phê', 4, 75, 'Mở lò nướng ở 170 độ C (340 độ F). Có thể lấy cục bơ lạt để thoa khắp khuôn vừa thơm, vừa dễ lấy. Bột khô bánh chiffon cam: Rây bột mì, bột bắp và bột nở cho mịn màng để qua 1 bên.', 'LgYQ_2.1.jpg', 'Cho lòng đỏ trứng, đường (Phần A), nước cam vắt và dầu vào một âu nhôm sạch rồi dùng lồng phới đánh cho đường tan hoàn toàn rồi cho tiếp cho hỗn hợp bột vừa rây vào âu trứng và trộn đều. Cho vỏ cam đã được bào sẵn vào âu rồi trộn đều. Để riêng âu trứng bột này qua một bên.', 'UKUD_2.3.jpg', 'Cho lòng trắng trứng vào một âu nhôm sạch khác. Dùng máy đánh trứng ở tốc độ trung bình cho đến khi trứng nổi các hạt li ti nhỏ như bọt xà phòng thì cho cream of tartar vào. Đánh tiếp hỗn hợp trứng ở tốc độ cao cho đến khi trứng đủ bông nhẹ và có vân.', 'jeBN_2.5.jpg', 'Lấy 75gr đường chia làm 3 phần, vừa cho vào hỗn hợp trứng và đánh tiếp ở tốc độ cao cho hỗn trứng vừa đạt chóp cứng (Stiff Peak). Đến đây thì dừng lại, nếu đánh tiếp trứng sẽ bông cứng và khó trộn. Lúc này trứng đã bông mịn, nổi vân, tạo thành chóp, chóp có thể hơi cong nhưng vẫn giữ được chóp. Cách làm bánh chiffon cam thành công hay không là ở bước này đó.', 'E9rL_2.7.jpg', 'Cho 1/4 hỗn hợp lòng trắng trứng vào âu lòng đỏ, dùng spatula trộn nhẹ cho hỗn hợp hoà quyện rồi lặp lại công đoạn này thêm 1 lần nữa. Cho hết phần lòng trắng trứng còn lại vào âu lòng đỏ. Dùng kĩ thuật fold để trộn cho hỗn hợp hoà quyện.', 'c9Lm_2.9.jpg', 'Cho phần bột vừa trộn vào khuôn bánh rồi lắc nhẹ khuôn cho bột dàn đều. Nướng bánh ở lò đã bật sẵn khoảng 45 đến 50 phút. Kiểm tra bánh chín bằng cách dùng ngón tay ấn nhẹ vào mặt bánh thấy bánh phồng trở lại ngay là được. Sau khi bánh đã chín tới thì lấy khuôn bánh ra khỏi lò. Úp một chiếc bát lên rack, rồi úp khuôn bánh lên chiếc bát. Để bánh chiffon cam nguội hoàn toàn. Cần phải úp bánh để bánh thoát ẩm.', 'RLuF_2.11.jpg', 'Sau khi bánh nguội, dùng dao nhon rọc quanh thành khuôn bánh chiffon cam và rọc nhẹ vào giữa lõi rồi dùng ngón tay trỏ đẩy nhẹ đáy bánh lên để lõi bánh rời khỏi khuôn. Dùng dao rọc nhẹ dưới đáy bánh rồi úp ngược bánh xuống để bánh rời hoàn toàn khỏi luôn. Khi dùng thì nên cắt ra từng phần nhỏ để vào đĩa rồi để phần bánh chiffon cam dư vào tủ lạnh để bảo quản nhé. Cách làm bánh chiffon hơi cầu kỳ phải không nào, nhưng mà ngon lắm đó, rất thích hợp cho các bạn mới bước chân vào bếp bánh nè.', '80g6_2.15.jpg', 3, 1, 0, 320, '2022-11-08 08:40:17', '2022-11-08 09:00:03'),
(19, 'Súp bắp cua ngon lành', 51, 15, 15, 'Dầu mè 2 muỗng canh,\r\n Tỏi 3 tép,\r\n Gừng 1 miếng,\r\n Bắp Mỹ 1 trái,\r\n Hành lá 1 cây,\r\n Thịt cua 200 gr,\r\n Nước dùng 400 ml\r\n(Nước hầm xương heo),\r\n Nước tương 2 muỗng canh,\r\n Tiêu 1/2 muỗng cà phê,\r\n Bột bắp 50 gr,\r\n Trứng gà 1 quả,\r\n Dầu ăn 2 muỗng canh,\r\n Muối 1 muỗng cà phê,\r\n Bột ngọt 1 muỗng cà phê,\r\n Ngò rí 1 nhánh', 2, 35, 'Làm nóng 2 muỗng canh dầu ăn trong một chảo to và sâu lòng. Cho gừng, tỏi băm vào phi thơm rồi cho bắp mỹ và hành lá vào, xào khoảng 2 phút.', '5Z56_3.6.jpg', 'Cho thịt cua vào xào cùng. Nêm 1 muỗng cà phê muối, 1 muỗng cà phê bột ngọt. Xào nhanh tay khoảng 1 phút.', 'Q32n_3.7.jpg', 'Cho nước dùng vào nấu cùng với bắp và cua. Nêm 2 muỗng canh nước tương.', 'H29h_3.8.jpg', 'Khi nước dùng sôi, hoà tan bột bắp trong 1/3 chén nước, cho vào nồi nước súp, khuấy đều. Trứng gà tách lấy lòng trắng trứng, rót từ từ vào nồi súp đang sôi, vừa rót vừa dùng muỗng khuấy cho lòng trắng tan đều. Sôi thêm 1 phút nữa thì tắt bếp.', 'UkWF_3.9.jpg', 'Múc súp bắp cua ra tô, cho thêm ngò rí, tiêu, dầu mè. Ăn ngay khi còn nóng.', 'A9wi_3-Súp bắp cua.jpg', '', '', '', '', 2, 1, 0, 320, '2022-11-08 08:55:55', '2022-11-08 08:59:54'),
(20, 'Bánh canh Nam Phổ xứ Huế tuyệt hảo', 2, 15, 8, 'Bột gạo 2 chén,\r\n Bột năng 1 chén,\r\n Nước 250 ml,\r\n Nước 2 chén,\r\n Tôm tươi 200 gr,\r\n Giò sống 100 gr,\r\n Thịt ba chỉ 200 gr,\r\n Mắm ruốc 1 muỗng cà phê\r\n(Huế)', 5, 40, 'Trước khi nấu bánh canh Nam Phổ cần: Rửa sạch tôm, luộc chín rồi bóc sạch vỏ tôm, cho vào cối giã nhỏ (giữ lại nước luộc tôm). Rửa sạch thịt ba rọi (thịt ba chỉ), bỏ da, cắt nhỏ rồi đem xay nhuyễn. Làm chả tôm thịt giò sống: Trộn đều thịt xay + giò sống + tôm giã nhỏ, nêm 1 muỗng hành củ tím băm, 1 muỗng cà phê đường, 1 muỗng cà phê tiêu, 1 muỗng canh hạt nêm, 1 muỗng canh nước mắm, 1 muỗng cà phê bột ngọt, 1 muỗng canh màu dầu điều, mang bao tay thực phẩm, nhồi cho chả quyện đều. Khi chả tôm thịt đã dẻo mịn, ướp 30 phút.', 'zONa_4.2.jpg', 'Làm dầu màu điều: cho hột màu điều vào chén, nấu sôi dầu rồi chế vào chén hột điều đó, rồi lọc lấy qua rây bỏ hột, được chén dầu điều. Cho ruốc vào 1/2 chén nước lạnh khuấy tan. Nhồi bột làm bánh canh xắt Nam Phổ: Cho bột gạo vào thau rồi đổ nước nấu thật sôi vào, dùng muỗng gỗ trộn đều đến khi bột đặc lại, sau đó nhồi cho bột thật dẻo mịn.', 'OLEt_4.4.jpg', 'Ngắt từng cục bột bánh canh nhỏ, cán bột mỏng vừa. Sau đó cắt bột thành từng sợi bánh canh xắt nhỏ vừa ăn.', 'uDmh_4.5.jpg', 'Cho chén bột năng + nước lạnh hoà đều quậy tan. Giúp nấu bánh canh Nam Phổ đặc sệt, hấp dẫn.', 'bm4V_4.7.jpg', 'Cách nấu bánh canh Nam Phổ Xứ Huế: Đun sôi nồi nước, cho sợi bánh canh xắt vào luộc. Khi sợi bánh canh xắt chín sẽ nổi lên và bột sẽ trong. Sau đó cho 1/2 chén nước bột năng đã pha vào nồi khuấy đều, đem xuống ngâm sợi bánh canh trong nồi như vậy 1 giờ cho sợi bánh canh xắt nở và mềm.', 'dPAC_4.8.jpg', 'Cho nồi nước luộc tôm lên bếp + nước ruốc đã lọc sạch cặn (cho nước ruốc vào từ lúc nước nguội), đổ thêm nước, rồi nấu sôi. Nước trong nồi sôi, lấy đĩa nhân ở bước 3 dùng tay tách nhỏ phần tôm thịt đó thả vào nồi cho đến hết, nêm nếm gia vị một lần nữa cho vừa ăn. Sau đó đổ từ từ nửa chén nước bột năng còn lại vào, quậy đều cho nồi nhân sánh lại (như nấu súp cua), rồi cho rau nêm vào + tiêu. Giữ nóng nồi bánh canh xắt với lửa nhỏ nhất, đổ nồi nước chả tôm thịt lên trên nồi bánh canh xắt', 'DVEs_4.10.jpg', 'Bánh canh Nam Phổ Xứ Huế ăn cùng hành, ngò, rau răm xắt nhỏ với chén nước mắm ớt mặn. Khi ăn, múc sợi bánh xắt ra tô trước rồi mới múc chả tôm thịt lên trên, rắc tiêu và nước mắm ớt, sau đó trộn đều lên, thưởng thức. Sợi bánh canh Nam Phổ mềm, thơm ngon vô cùng, ăn rất ấm bụng, thích hợp cho bữa ăn ngày lạnh.', '2MPx_4.11.jpg', 1, 1, 0, 321, '2022-11-08 09:05:53', '2020-05-17 11:13:01'),
(25, 'Bánh canh trứng cút', 60, 2, 10, 'Bột gạo 400 gr,\nXương heo 300 gr,\nSườn non 300 gr,\nTrứng cút 20 quả,\nHành lá 2 cây,\nNgo rí 2 nhánh,\nHạt nêm 1 muỗng canh,\nMuối 1/2 muỗng cà phê,\nBột ngọt 1/2 muỗng cà phê,\nTiêu 1/2 muỗng cà phê', 2, 30, 'Cách nấu nước dùng bánh canh xương heo: Cho xương vào nồi, nấu khoảng 1 tiếng. Nêm hạt nêm, muối, bột ngọt, tiêu. Chú ý hớt bọt để nước dùng trong. Khi thấy nồi nước đã ngọt, vớt xương ra, cho sườn vào đun thêm 1 lúc cho sườn đủ chín là được.', '1.1.jpg', 'Dùng 1 chiếc vá như hình trên cho bột vào, rồi nhúng vào nồi nước. Chú ý nhúng sao cho bột ngập nước. Đợi sôi 2 phút.', '1.2.jpg', 'Sau khi sợi bánh canh chín thì cho vào tô. Xếp trứng cút lên, múc nước sườn vào. Rắc hành lá và ngò rí. Nếu thích cay có thể cho thêm ớt. Bánh canh xương trứng cút với hương vị đậm đà, thơm ngon, mời cả nhà ăn sáng rất hấp dẫn đấy nhé!', '1.3.jpg', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 1, 1, 0, 320, NULL, NULL),
(26, 'Xôi vò', 61, 2, 5, 'Gạo nếp 500 gr,\nĐậu xanh 250 gr,\nMuối 1/2 muỗng cà phê,\nĐường trắng 2 muỗng canh,\nNước cốt dừa 50 ml\n', 4, 60, 'Ngâm đậu xanh qua đêm rồi vo sạch, cho vào nồi nấu chín với 1/2 muỗng cà phê muối', '1.4.jpg', 'Dùng chày giã nhuyễn đậu xanh đã chín rồi vo lại thành từng nắm.\nBạn cũng có thể dùng muỗng tán nhuyễn mịn đậu xanh nếu không có chày nhé! Xôi vò ngon chính là chọn được đậu xanh còn mới, hạt nhỏ, không bị lẫn nhiều tạp chất. (2.2; 2.3)', '1.5.jpg', 'Vo sạch gạo nếp, ngâm qua đêm, để ráo nước.\nBước ngâm gạo nếp qua đêm rất quan trọng. Nhờ bước này, xôi vò khi nấu lên sẽ được dẻo, xốp mềm và không bị sượng.', '1.6.jpg', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 1, 1, 0, 320, NULL, NULL),
(28, 'Bánh canh thịt vịt', 63, 2, 14, 'Thịt vịt 1/2 con,\nNấm bào ngư 300 gr,\nGừng 1 củ,\nHành lá 2 cây,\nRượu trắng 200 ml,\nHạt nêm 1/2 muỗng cà phê,\nMuối 1 1/2 muỗng cà phê,\nĐường trắng 1 muỗng cà phê,\nHành tím băm 1 muỗng cà phê,\nNgò rí 10 gr,\nDầu điều 2 muỗng cà phê,\nBột gạo 400 gr,\nBột năng 50 gr,\nTiêu 1/2 muỗng cà phê\n', 4, 40, 'trắng lên khắp thân vịt, để khoảng 20 phút sau đó rửa lại cho thật sạch, để vịt lên rổ cho ráo nước.', '1.7.jpg', 'Phần vịt sau khi ráo nước, cho ra thau lớn, thêm hành tím băm, 1 muỗng cà phê muối, đường, hạt nêm, tiêu và một ít gừng giã nhuyễn, trộn đều, ướp khoảng 2-3 tiếng.', '1.8.jpg', 'Đun nóng dầu điều, phi tỏi thơm, cho hỗn hợp thịt vịt đã ướp vào nồi, đun lửa lớn đến khi sôi thì đun lửa nhỏ, đổ 1.5 lít nước lạnh vào nồi đun sôi đến khi vịt mềm.', '1.9.jpg', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 3, 1, 0, 320, NULL, NULL),
(29, 'Bánh canh chả cá', 64, 2, 12, 'Bột gạo 2 chén,\nBột năng 250 gr,\nPhi lê cá 500 gr(Cá thác lác),\nỚt 1 trái,\nXương ống 1 kg,\nHành lá 1/2 cây,\nNước mắm 2 muỗng canh,\nĐường trắng 1 muỗng cà phê,\nTiêu 1/2 muỗng cà phê,\nNgò rí 5 gr,\nHạt nêm 1 muỗng cà phê,\nMuối 1/2 muỗng cà phê', 4, 45, 'Ninh nước dùng từ xương lợn khoảng 2 giờ, trong quá trình đun nhớ vớt bọt để nước dùng được trong.', '1.11.jpg', 'Trộn bột năng với bột gạo với nhau. Đun nước sôi, rưới đều lên bột rồi nhồi đến khi hỗn hợp bột đó mềm mịn, dai là được. Chia bột thành từng viên tròn bằng nắm tay, dùng chai cán mỏng rồi thái thành từng sợi to bằng đầu đũa.\nĐể tiết kiệm thời gian làm bánh canh chả cá, bạn có thể tìm mua bánh canh bột gạo bán sẵn ở các chợ và siêu thị.(4.2)', '1.12.jpg', 'Quết nhuyễn thịt phi lê cá với đầu hành, muối, đường, hạt nêm, tiêu. Quết đến khi thấy phần thịt cá dai, dẻo dính vào nhau là được.\nCách làm bánh canh chả cá cực nhất là ở bước này đó. Quết càng mạnh tay và càng lâu thì chả cá càng dai ngon.', '1.13.jpg', 'Vớt bỏ phần xương trong nước dùng bánh canh chả cá, nêm nước mắm, hạt nêm vào nước dùng cho vừa ăn. Dùng muỗng chia từng viên chả cá thành miếng vừa ăn rồi cho vào nồi nước dùng, khuấy đều.\nNếu có thời gian thì có thể chiên hoặc hấp chả cá để bánh canh chả cá hấp dẫn hơn', '1.14.jpg', 'Đun sôi một nồi nước khác, cho sợi bánh canh vào chần sơ qua. Xả lại bằng nước lạnh rồi để ráo. Khi nước dùng bánh canh chả cá đã chuẩn bị xong, cho tiếp sợi bánh canh đã chần vào rồi đun sôi, nêm nếm lại cho hợp khẩu vị gia đình nếu cần', '1.15.jpg', 'Múc bánh canh chả cá ra tô và thưởng thức. Bánh canh chả cá dùng nóng với ít hành ngò, thêm chén nước mắm ớt kế bên là hết sảy. Vào những ngày mưa hay tiết trời se lạnh được măm măm 1 tô bánh canh chả cá nóng hôi hổi thì còn gì bằng! Cuối tuần các bạn nhất định phải thử cách làm bánh canh chả cá này để đãi gia đình nhé!\nCho xương lợn vào túi lưới rồi mới cho vào nồi hầm. Túi lưới sẽ giúp nước dùng bánh canh chả cá không bị lẫn những mảnh xương nhỏ, ăn ngon hơn.', '1.16.jpg', NULL, NULL, 1, 1, 0, 320, NULL, NULL),
(30, 'Bánh canh cá lóc Quảng Trị', 65, 2, 7, 'Cá 1 con,\nBột gạo 300 gr,\nKẹo caramel 200 lá,\nĐường 200 gr,\nTiêu 50 gr,\nCá xay 500 gr,\nỚt 3 trái', 4, 30, 'Chặt cá lóc thành miếng vừa ăn, rửa sạch nguyên liệu. Luộc cá vừa chín tới, vớt cá ra ngoài. Phi hành cùng ớt chín vàng. Cho hỗn hợp vừa phi chín tới vào nước cá đã luộc.', '1.17.jpg', 'Cách nấu bánh canh cá lóc: Tách thịt cá ra khỏi xương lựa phần nạc bỏ riêng sang 1 tô, tiếp tục cho nước đun sôi trong 10 phút. Cho cá xay đã vo viên tròn (nếu các bạn khéo tay tạo hình cho cá viên thành hình tròn sẽ ngon mắt hơn) vào trong nấu chín. Cho gia vị đã chuẩn bị vào nêm vừa ăn. Chú ý: nêm tiêu vào lúc cuối cùng vì tiêu chính là hương vị chính làm dậy mùi của món ăn.', '1.18.jpg', 'Cho hành lá đã thái nhỏ lên bên trên, trang trí ra tô. Vậy là đã có món bánh canh cá lóc đúng điệu đất Quảng Trị rồi. Hãy lưu lại cách làm bánh canh cá lóc đặc trưng của vùng đất Quảng Trị nhé. Hứa hẹn sẽ là món banh cá lóc đổi vị cho cả nhà cuối tuần đấy.', '1.19.jpg', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 4, 1, 0, 320, NULL, NULL),
(31, 'Bánh burger thịt heo', 66, 2, 11, 'Thịt heo bằm 283 gr,\nBánh mì 2 cái(Burger),\nSốt tương đen 2 muỗng canh,\nGừng 1 miếng,\nGiấm 1 muỗng canh,\nCủ cải đỏ 85 gr,\nSốt Mayonnaise 2 muỗng canh,\nBông cải xanh 2 1/4 gr,\nMuối 1 1/2 muỗng cà phê,\nTiêu 1 1/2 muỗng cà phê,\nDầu olive 1 muỗng canh', 2, 25, 'Rửa sạch củ cải đỏ, bông cải xanh, để ráo nước. Bông cải xanh cắt bỏ phần thân, thái nhỏ. Gừng rửa sạch, gọt vỏ, thái nhỏ. Bánh burger cắt làm đôi. Củ cải thái lát mỏng, cho củ cải vào bát ướp cùng giấm và dầu olive. Trong một chiếc bát nhỏ, cho mayonnaise, 1/2 phần sốt tương đen, 1/2 muỗng cà phê muối và hạt tiêu vào khuấy đều, để riêng.', '1.21.jpg', 'Chế biến bông cải xanh: Cho bông cải xanh lên khay nướng, cho dầu olive, 1/2 muỗng cà phê muối và hạt tiêu vào trộn đều. Cho khay súp lơ vào lò và nướng khoảng 16-18 phút hoặc đến khi bông cải chín, chuyển màu. Tắt lò, lấy khay bông cải ra, cho dầu mè vào trộn đều.\nCách làm hamburger thịt heo này ngon hơn là nhờ có bông cải nướng này đó, các bạn không được bỏ qua nha !', '1.22.jpg', 'Cách làm nhân bánh hamburger: Trong một chiếc bát, cho thịt heo xay, gừng thái nhỏ, phần nước sốt tương đen còn lại, thêm 1/2 muỗng cà phê muối và hạt tiêu vào trộn đều. Chia hỗn hợp thịt thành 2 phần bằng nhau có độ dày khoảng 1,25cm. Để riêng ra đĩa. Đun nóng chảo cùng chút dầu olive, cho thịt heo đã viên vào chiên mỗi mặt khoảng 3 - 5 phút hoặc cho đến khi thịt chín, sém cạnh. Gắp ra đĩa, dùng giấy bạc bọc lại để thịt nóng.', '1.23.jpg', 'Nướng bánh burger: Cho bánh mì vào áp chảo khoảng 1 - 2 phút cho đến khi bánh mì sém cạnh. Gắp ra đĩa để riêng. Cho bánh mì lên đĩa, rưới nước sốt lên trên bánh mì, xếp thịt heo rồi củ cải đỏ lên rồi kẹp miếng bánh còn lại lên trên cùng. Ăn bánh burger cùng súp lơ xanh vừa nướng sẽ rất ngon, lại đầy đủ dinh d', '1.24.jpg', 'Bánh burger thịt heo với lớp vỏ mềm, củ cải giòn, nước sốt béo ngậy bọc lấy nhân thịt heo xay đậm đà, ăn kèm bông cải xanh nướng vừa ngon vừa đầy đủ dinh dưỡng. Cách làm bánh burger heo lại không hề khó, các bạn có thể tự làm ở nhà để giúp bữa ăn sáng của gia đình thêm phần thú vị nha !', '6-Bánh burger thịt heo.jpg', NULL, NULL, NULL, NULL, 2, 1, 0, 320, NULL, NULL),
(32, 'Bánh canh cá lóc miền Tây', 67, 2, 13, 'Cá lóc 500 gr,\nHạt nêm 1/2 muỗng canh,\nMuối 1/2 muỗng canh,\nCà chua 1 trái,\nNgo rí 20 gr,\nTiêu 1 muỗng cà phê,\nHành tím 1 củ,\nChả cá 500 gr,\nHành lá 20 gr,\nHẹ 20 gr,\nGiá đỗ 20 gr,\nNước 2 lít,\nBánh canh 400 gr', 4, 25, 'Sơ chế cá lóc, thái mỗi con làm 5 phần. Lưu ý cá lóc khi mua về nên được xát chanh hoặc muối để cá sạch bạn nhé. Bỏ cá lóc vào nồi và đổ khoảng 200ml nước (hoặc 1/3 dung tích nồi nấu). Bật lửa nấu trong khoảng 10 - 15 phút sau đó tắt bếp và vớt cá ra, để ráo khoảng 5 phút.\nSở dĩ không đổ nước vào nhiều là do thịt cá rất dễ bị bở, ngoài ra bước này bạn khoan hãy phi hành. Một số ý cho rằng nên chiên cá hoặc phi hành sẽ khử được mùi tanh, thế nhưng như vậy sẽ khiến bánh canh của bạn trở nên đầy dầu mỡ. Chỉ cần xát chanh và muối ở bước sơ chế là được rồi bạn nhé, cá sẽ giữ được độ ngọt và mềm.', '1.25.jpg', 'Chuẩn bị nguyên liệu đi kèm, bao gồm: chả cá, giá, hẹ, hành, ngò, cà chua và bánh canh. Chả cá mua ngoài hay bị gắt dầu, tốt hơn hết hãy chần sơ qua nước nóng bạn nhé. Rửa sạch giá, hẹ, hành, ngò và cà chua. Sau đó thái hành, hẹ ra từng khúc, cà chua xắt dọc làm 4 để nấu cùng nước lèo. Về bánh canh, khi mua về hãy chịu khó chần qua nước đun sôi bạn nhé, bánh sẽ ngon hơn và khử được mùi chua vốn có.\nCần rửa sạch gốc hẹ và hành bạn nhé, nên dùng muối để ngâm rau.', '1.26.jpg', 'Nấu bánh canh cá lóc miền Tây: dùng lại phần nước đã luộc cá trước đó, đổ gần đầy nồi nấu, bỏ cà chua đã thái vào nồi. Cho 1/2 muỗng canh muối và 1/2 muỗng canh hạt nêm, tùy bạn thích ăn mặn ngọt như nào có thể bổ sung thêm nhé.\nGiá, hẹ nên được trần riêng ở nồi nước sôi khác nhé, để tránh nước lèo của bạn có vị \"lạ\".', '1.27.jpg', 'Đậy nắp đung sôi và trong thời gian này bạn có thể tiến hành rút xương cá, chuẩn bị tô cho cả nhà rồi! Tăng hương vị cho nước dùng thông qua hành tím phi và chút ngò, hành nhé.Khi hành đã được phi xong, bạn tắt bếp và bỏ ngay vào chảo ngò và tiêu nhé, sau đó bỏ hỗn hợp vào nồi nước dùng. Đây là bí kíp khá hay để tăng hương thơm cho các loại nước dùng đấy!', '1.28.jpg', 'Bước cuối cùng là đổ nước dùng vào tô đã chuẩn bị sẵn. Bánh canh cá lóc thành phẩm có hương vị thơm ngon, ngọt tự nhiên của cả lóc. Đặc biệt với cách nấu bánh canh cá lóc miền Tây này, nước dùng sẽ cực đậm vị, lại giúp thanh nhiệt cho ngày nắng nóng. Chúc cả nhà thành công!', '1.29.jpg', NULL, NULL, NULL, NULL, 4, 1, 0, 320, NULL, NULL),
(33, 'Bánh burger mini', 68, 2, 7, 'Bột mì 200 gr,\nBơ 14 gr,\nMuối 5 gr,\nĐường trắng 16 gr,\nTrứng gà 1 quả,\nSữa tươi 100 gr,\nMè trắng 1 chén', 2, 100, 'Trộn bột làm bánh burger: bột mì, bơ, muối, đường, trứng, sữa tươi vào trộn đều với nhau rồi đem ủ ở nhiệt độ thường khoảng 1 tiếng.', '1.31.jpg', 'Sau đó chia bột thành 9 phần đều nhau, tiếp tục ủ trong 50 phút nữa trong nhiệt độ (35-40 độ C).', '1.32.jpg', 'Sau khi ủ bột xong, rắc mè trắng lên trên rồi đem nướng ở 200 độ C trong 10 phút.', '1.33.jpg', 'Bánh gurger chín vàng đem ra. Tròn tròn, nhỏ nhắn, thơm thơm, rất tiện để mang đi ăn trong các buổi dã ngoại nữa đấy! Bánh burger sau khi chia bột xong các bạn có thể bọc lại để tủ lạnh sáng rã đông rồi ủ lần 2 cho lớn là nướng bình thường rồi, rất tiện phải không nào. Có thể cắt đôi, kẹp rau xà lách, thịt nguội, phô mai vào trong, ăn chả khác gì ngoài tiệm luôn.', '1.34.jpg', NULL, NULL, NULL, NULL, NULL, NULL, 1, 1, 0, 320, NULL, NULL),
(34, 'Bánh canh cá lóc', 69, 2, 11, 'Cá lóc 1 kg,\nChả cá thác lác 200 gr,\nBánh canh 1 kg,\nMuối 15 gr,\nNước mắm 50 ml,\nHạt nêm 50 gr,\nĐường trắng 30 gr,\nHành lá 1 cây,\nHành boa rô 1 cây,\nGừng 1 miếng,\nTiêu 1/2 muỗng cà phê\n', 4, 25, 'Rửa sạch thịt cá và xương. Rửa sạch, cắt lát gừng. Đặt một chiếc nồi lên bếp, cho 1.2 lít nước sạch, xương cá cùng gừng vào, mở lửa vừa và nấu nước dùng.\nCách làm bánh canh cá lóc ngon luôn không thể thiếu nồi nước dùng tuyệt hảo được đâu nhé.', '1.35.jpg', 'Nấu đến khi nước bắt đầu sôi, thả thịt cá vào và nêm 15gr muối, 50gr hạt nêm, 30gr đường và 50ml nước mắm. Để lửa nhỏ, nấu thêm khoảng 20 phút rồi tắt bếp. Vớt thịt cá lóc ra, cắt thành từng miếng vừa ăn.', '1.36.jpg', 'Đặt một chiếc nồi nhỏ cùng một ít nước sạch lên bếp, mở lửa lớn. Khi nước sôi, thả bánh canh vào trụng đến khi sợi bánh trong thì vớt ra. Xếp bánh canh, cá lóc, chả cá ra tô, chan nước dùng lên. Rắc thêm một ít hành lá, hành boa rô và tiêu lên để tô bánh canh cá lóc tăng thêm hương vị.', '1.37.jpg', 'Đặt một chiếc nồi nhỏ cùng một ít nước sạch lên bếp, mở lửa lớn. Khi nước sôi, thả bánh canh vào trụng đến khi sợi bánh trong thì vớt ra. Xếp bánh canh, cá lóc, chả cá ra tô, chan nước dùng lên. Rắc thêm một ít hành lá, hành boa rô và tiêu lên để tô bánh canh cá lóc tăng thêm hương vị', '1.38.jpg', NULL, NULL, NULL, NULL, NULL, NULL, 1, 1, 0, 320, NULL, NULL),
(35, 'Bánh canh cá bớp', 70, 2, 18, 'Cá bớp 500 gr,\nXương heo 300 gr,\nCà rốt 200 gr,\nCủ cải trắng 200 gr,\nNấm rơm 150 gr,\nTrứng cút 10 quả,\nBánh canh 300 gr(Tươi),\nHành lá 50 gr,\nNgo rí 50 gr,\nHành tím 50 gr,\nĐường phèn 50 gr,\nMuối 1 muỗng cà phê,\nNước mắm 2 muỗng cà phê,\nBột canh 1 muỗng canh,\nTiêu 1 muỗng cà phê,\nDầu ăn 1 muỗng canh,\nỚt 4 quả,\nChanh 1 trái', 4, 55, 'Chặt nhỏ xương heo, rửa sạch, chần qua nước sôi. Sau đó, vớt xương ra rồi cho lượng nước lọc vừa đủ vào nồi để hầm xương.', '1.39.jpg', 'Gọt vỏ cà rốt, củ cải, rửa sạch tỉa hoa (tùy thích). Rửa sạch nấm rơm, ngâm nước muối 15 phút rồi vớt ra để ráo. Rửa sạch cá bóp thái miếng nhỏ vừa rồi ướp với muối. Luộc trứng cút, bọc vỏ. Cọng bánh canh chần sơ qua nước sôi 2 phút rồi vớt ra để ráo. Hành tím phi thơm, ớt đỏ thái khoanh.', '1.41.jpg', 'Khi nồi xương hầm sôi khoảng 20 phút thì tiếp tục cho cà rốt và củ cải vào nấu cho đến khi chín mềm. Kế tiếp, cho cá bóp vào nấu tiếp trên lửa vừa.', '1.42.jpg', 'Khi nồi nước dùng sôi lên lần nữa thì cho nấm rơm vào rồi nêm nếm gia vị muối, bột canh, đường phèn theo liều lượng tùy khẩu vị gia đình. Tiếp đó, cho hành lá và tiêu xay vào thì tắt bếp.', '1.43.jpg', 'Cho cọng bánh canh vào tô rồi gắp cá bóp với cà rốt, củ cải, hành ngò vào tô rồi chan nước dùng lên, dùng nóng. Nước dùng thanh ngọt, đậm đà với từng miếng cá tươi ngon, mềm thơm, béo ngậy quyện với những cọng bánh canh trắng ngần, thật hấp dẫn. Đây là món bánh canh cá bớp tươi, thơm ngon, dễ nấu, bạn có thể nấu cho cả nhà thưởng thức mà không tốn quá nhiều thời gian.', '1.44.jpg', NULL, NULL, NULL, NULL, 1, 1, 0, 320, NULL, NULL),
(36, 'Bánh canh bột xắt nước dừa nấu tôm', 71, 2, 6, 'Dừa nạo 300 gr,\nTôm tươi 300 gr(Tôm đất),\nBánh canh 500 gr(Bột gạo),\nNước mắm 1/2 muỗng canh,\nBột ngọt 1/2 muỗng cà phê,\nMuối 1/2 muỗng cà phê\n', 2, 25, 'Cách nấu bánh canh bột gạo nước dừa: Cho nồi nước cốt dừa lên bếp, nấu sôi vừa. Nêm muối. Sau đó cho bánh canh vào. Cho tôm vào đảo đều tay, để tôm chín sẽ có màu đỏ cam rất đẹp. Nêm bột ngọt.\nKhuấy đều tay, nếu ko bánh canh sẽ dính nồi', '1.45.jpg', 'Múc bánh canh bột xắt nước dừa nấu tôm ra tô, cho hành ngò vào. Nêm mỗi tô 1/2-1 muỗng canh nước mắm tùy theo khẩu vị. Với cách nấu bánh canh bột gạo nước dừa này, cực đơn giản nhưng lại rất thơm ngon đấy nhé. Còn lăn tăn gì mà không làm ngay món bánh canh bột xắt mời cả nhà nào!', '1.46.jpg', 'Cách làm bánh canh bột lọc: Cho bột năng vào một mặt phẳng rộng, tạo một trũng sâu ở giữa. Đổ từ từ nước nóng vào giữa, dùng tay trộn đều thành một hỗn hợp dẻo, mềm.', '1.47.jpg', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2, 1, 0, 320, NULL, NULL);
